/* eslint-disable jsx-a11y/img-redundant-alt */
import React from "react";
import {
  Link,
  useRouteMatch,
  Switch,
  Route,
  useParams,
} from "react-router-dom";
import Search from "../components/Search";
import { useStateValue } from "../StateProvider";
import response from "../store/response"; // Import the response data directly
import "./SearchPage.css";
import SearchIcon from "@material-ui/icons/Search";
import DescriptionIcon from "@material-ui/icons/Description";
import ImageIcon from "@material-ui/icons/Image";

function SearchPage() {
  const [{ term }, dispatch] = useStateValue();

  const { path, url } = useRouteMatch();

  // Use the data from response directly
  const data = response;

  console.log(data);
  return (
    <div className="searchPage">
      {/* Header */}
      <div className="searchPage_header">
        {/* ... (rest of the header code) */}
      </div>
      {/* Routing header options */}
      <Switch>
        <Route exact path={path}>
          {/* Results */}
          {term ? (
            <div className="searchPage_results">
              <p className="searchPage_resultsCount">
                About {data?.searchInformation.formattedTotalResults} results (
                {data?.searchInformation.searchTime} seconds) for {term}
              </p>
              <div className="searchPage_resultsContainer">
                <div className="searchPage_resultsColumn">
                  {/* Render the left column */}
                  {data?.items.slice(0, data.items.length / 2).map((item) => (
                    <div className="searchPage_result" key={item.link}>
                      {/* ... (rest of the result item code) */}
                    </div>
                  ))}
                </div>
                <div className="searchPage_resultsColumn">
                  {/* Render the right column */}
                  {data?.items.slice(data.items.length / 2).map((item) => (
                    <div className="searchPage_result" key={item.link}>
                      {/* ... (rest of the result item code) */}
                    </div>
                  ))}
                </div>
              </div>
            </div>
          ) : (
            <div className="searchPage_results emptyMessage">
              Please type something in the search box to search something.
            </div>
          )}
        </Route>
        <Route path={`${path}/:optionId`}>
          <Option />
        </Route>
      </Switch>
      <response />
    </div>
  );
}

export default SearchPage;
